import React, { Component } from "react";
import "./Homepage.css";

import axios from 'axios';

export default class Homepage extends React.Component {
  render() {
    return (
      <div className="Homepage-content">
        <NavBar />
        <Context />
        <BodyContent />
      </div>
    );
  }
}

class NavBar extends Component {
  render() {
    return (
      <ul className="nav">
        <li>
          <a className="active" href="#home">
            Home
          </a>
        </li>
      </ul>
    );
  }
}

class Context extends Component {
  render() {
    return (
      <div className="banner-container">
        <div className="banner-content">
          <h1>Post Picker</h1>
          <a href="https://www.ethicmarketplace.us/" target="_blank" id="visit">
            Visit
          </a>
        </div>
      </div>
    );
  }
}


class BodyContent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [""]
    }
  }

  componentDidMount() {
    axios.get('https://ethic-blueprint.herokuapp.com/articles')
    .then (result => this.setState(() => ({data: result.data['articles']})))
    .catch(error => console.log(error))
  }

  render() {

    const bodyComponents = this.state.data.map((d, id) => {return(
    <BodyComponent
      key={id}
      id={d.id}
      title={d.title}
      img={d.image_url}
      content={d.content}
      url={d.url}
      author={d.author}
      publish_date={d.publish_date}
    />)})

    return (
        <div className="body-content">
            {bodyComponents}
        </div>
    );
  }
}

class BodyComponent extends Component {
  constructor(props) {
    super(props);
  }

  postArticle(article_id) {
    const json = {
      id: article_id
    };
    axios.post(`https://ethic-blueprint.herokuapp.com/shopify/articles`,{json})
  }

  render() {
    console.log(this.props.url)
    return (
      <div className="article-list">
        <div className="title">{this.props.title}</div>
        <div className="author">{this.props.author}</div>
        <img className="article-img" onClick={() => window.open(this.props.url)} src={this.props.img } />
        <div className="date">{this.props.publish_date}</div>
        <div className="description">{this.props.content}</div>
        <button className="visit-article" onClick={() => window.open(this.props.url)}>Visit Article</button>
        <button className="add" onClick={() => this.postArticle(this.props.id)} style={{ marginTop: 10 }}>
          Add To Blog
        </button>
      </div>
    );
  }
}
